import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button, Card, Container, Modal, Form } from 'semantic-ui-react';
import FormationService from './Services/FormationService';

const options = [
    { key: '1', text: 'en ligne ', value: 'en ligne ' },
    { key: '2', text: 'presentiel ', value: 'presentiel' },
];

class ListFormations extends Component {

    constructor(props) {
        super(props);
        this.state = {
            formations: [],
            open: true
        }
    }

    componentDidMount() {
        FormationService.getFormations().then(response => {
            this.setState({
                formations: response.data
            })
        })
    }

    handleDelete(id) {
        FormationService.deleteFormation(id).then(() => {
            alert("Formation supprimée.");
        });
    }

    updateFormation(formation) {
        FormationService.updateFormation(formation).then(() => {
            alert("Formation modifiée");
        });
    }

    render() {
        return (
            <Card.Group>
                {
                    this.state.formations.map(
                        formation => {
                            return (
                                <Card>
                                    <Card.Content>
                                        <Card.Header>{formation.titre}</Card.Header>
                                        <Card.Meta>
                                            Prix: {formation.prix}Dt<br></br>
                                            {formation.type}<br></br>
                                            Durée: {formation.duree} jours<br></br>
                                            Catégorie: {formation.categorie}
                                        </Card.Meta>
                                        <Card.Description style={{
                                            fontSize: '20px'
                                        }}>
                                            {formation.description}
                                        </Card.Description>
                                        <br></br>
                                        <Container textAlign='right'><strong> Débute {formation.dateDebut}</strong></Container>
                                    </Card.Content>
                                    <Card.Content extra>
                                        <Button inverted color='red' onClick={() => this.handleDelete(formation.id)}>Supprimer</Button>

                                        <Modal size='tiny' style={{
                                            backgroundColor: 'darkred'
                                        }}
                                            onClose={() => setOpen(false)}
                                            onOpen={() => setOpen(true)}
                                            open={this.state.open}
                                            trigger={<Button inverted color='blue'>Modifier</Button>}
                                        >
                                            <Modal.Header style={{
                                                textAlign: 'center'
                                            }}>MODIFIER FORMATION</Modal.Header>
                                            <Modal.Content>
                                                <Form>
                                                    <Form.Field>
                                                        <label>Titre de formation</label>
                                                        <input placeholder='Titre de formation' name='titre' value={formation.titre} />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Nom de formateur</label>
                                                        <input placeholder='Nom de formateur' name='nom_formateur' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Type de formation</label>
                                                        <Form.Select
                                                            fluid
                                                            options={options}
                                                            placeholder='Type de formation'
                                                            name='type'
                                                            value={this.state.selectValue}
                                                            onChange={this.handleChange}
                                                        />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Nombre de place max</label>
                                                        <input type='number' placeholder='Nombre de condidat maximum...' name='nbr_condidat_max' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Prix</label>
                                                        <input placeholder='Prix de formation...' name='prix' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <Form.Input label='Date de debut' name='date_debut' type="date" placeholder='Date ...' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Durée</label>
                                                        <input type='number' placeholder='Durée (En jours)' name='duree' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>Catégorie</label>
                                                        <input placeholder='Catégorie...' name='categorie' />
                                                    </Form.Field>
                                                    <Form.Field>
                                                        <label>About</label>
                                                        <input placeholder='Desscription du formation' name='description' />
                                                    </Form.Field>
                                                    <Link to="/ListFormations"><Button inverted color='blue' type='submit' style={{
                                                        position: 'relative',
                                                        left: '40%'
                                                    }}>Confirmer</Button></Link>
                                                </Form>
                                            </Modal.Content>
                                        </Modal>
                                    </Card.Content>
                                </Card>
                            )
                        }
                    )
                }
            </Card.Group>
        );
    }
}

export default ListFormations;
