import React, { Component } from 'react'
import { Menu, Image, Header, Sticky } from 'semantic-ui-react'

export default class Commun extends Component {
    state = {}

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state

        return (
            <div>
                <div className='content-container'>
                    <header>
                        <Sticky>
                            <Menu fixed='top'
                                borderless
                                style={{
                                    backgroundColor: '#115ff9',
                                }}
                                color='yellow'
                            >

                                <Menu.Item >
                                    <Image src='images/logo.png'
                                        size='tiny'
                                        style={{
                                            borderRadius: '100%',
                                            backgroundColor: 'white'
                                        }}
                                        inverted
                                    />
                                </Menu.Item>

                                <Menu.Item header
                                >
                                    <Header
                                        as='h2'
                                        style={{
                                            fontFamily: 'Arial, sans-serif',
                                            color: 'white',
                                            fontWeight: 900
                                        }}
                                    >
                                        CENTRE DE FORMATIONS
                                    </Header>
                                </Menu.Item>

                                <Menu.Item position='right'>
                                    <Menu style={{
                                        backgroundColor: '#115ff9',
                                    }}
                                    >

                                        <Menu.Item
                                            name='home'
                                            active={activeItem === 'home'}
                                            onClick={this.handleItemClick}
                                            style={{
                                                fontFamily: 'Arial, sans-serif',
                                                color: 'white',
                                                fontWeight: 700
                                            }}
                                        >
                                            Home
                                        </Menu.Item>

                                        <Menu.Item
                                            name='a-propos'
                                            active={activeItem === 'a-propos'}
                                            onClick={this.handleItemClick}
                                            style={{
                                                fontFamily: 'Arial, sans-serif',
                                                color: 'white',
                                                fontWeight: 700
                                            }}
                                        >
                                            A propos
                                        </Menu.Item>

                                        <Menu.Item
                                            name='formations'
                                            active={activeItem === 'formations'}
                                            onClick={this.handleItemClick}
                                            style={{
                                                fontFamily: 'Arial, sans-serif',
                                                color: 'white',
                                                fontWeight: 700
                                            }}
                                        >
                                            Formations
                                        </Menu.Item>
                                    </Menu>
                                </Menu.Item>
                            </Menu>
                        </Sticky>
                    </header>
                </div>
                <footer className='footer-pin'>
                        <h1></h1>
                </footer>
            </div>
        )
    }
}
