import React, { Component } from 'react'
import { Menu,Input, Segment } from 'semantic-ui-react'
import { Form } from 'semantic-ui-react'



   const options = [
  { key: '1', text: 'en ligne ', value: 'en ligne ' },
  { key: '2', text: 'presentiel ', value: 'presentiel' },
]  

export default class Admin extends Component {


  constructor(props){
    super(props);
    this.state = {
      titre : '',
      dateDebut: null,
      duree: 0,
      type: '',
      prix: 0,
      etat: 'non terminée',
      nbrCandidat: '',
      nbrCandidatMax: 0,
      categorie: '',
      description: ''            
    }
  }


    handleItemClick = (e, { name }) => this.setState({ activeItem: name });
    handleChange = (e) => this.setState({ 
      [e.target.name]: e.target.value
    });


    render() {
      const { activeItem, titre, dateDebut, duree, type, prix, etat, nbrCandidat, nbrCandidatMax, categorie, description} = this.state
      var jsx;
      if (activeItem ==='ajouter une formation ') {
        jsx =<Segment attached='bottom'><p>Ajouter une formation:</p>
              <Form>
        <Form.Group widths='equal'>
          <Form.Input fluid label='Titre de formation' name='titre' onChange={this.handleChange} placeholder='Titre de formation' /><p>{titre}</p>
          <Form.Input fluid label='Nom de formateur' value="" placeholder='Nom de formateur ' />
          <Form.Select
            fluid
            label='type de formation'
            options={options}
            placeholder='type de formation'
          /><p>{type}</p>
        </Form.Group>
        <Form.Group widths='equal'>
          <Form.Input fluid label='Nombre de place max' placeholder='Nombre de condidat maximum...' />
          <Form.Input fluid label='Prix' value={nbrCandidatMax} onChange={this.handleChange} placeholder='Prix du foramtion...' />
          <Form.Input fluid label='Date de debut' type="date" placeholder='Date ...' /> 
        </Form.Group>
       <Form.Group inline>
       <label>Categorie </label>
          <Input placeholder='Categorie...' />

       </Form.Group>
        
        <Form.TextArea label='About' placeholder='Description du formation...' />
        
        <Form.Button>Submit</Form.Button>
      </Form>
      <p>{nbrCandidatMax}</p>


        </Segment>;
      }
      return (
        <div>
        <Menu attached='top' tabular>
          <Menu.Item
            name='liste de formation'
            active={activeItem === 'liste de formation'}
            onClick={this.handleItemClick}
           />
        
        
          <Menu.Item
            name='ajouter une formation '
            active={activeItem === 'ajouter une formation '}
            onClick={this.handleItemClick}
          />
        </Menu>
          {jsx}
        </div>
      )
    }
    
}
