import React, { Component } from 'react'
import { Menu, Image, Header } from 'semantic-ui-react'

export default class Commun extends Component {
    state = {}

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state

        return (
            <Menu
                borderless
                style={{
                    backgroundColor: '#608cf9',
                }}
            >

                <Menu.Item>
                    <Image src='https://www.graphicsprings.com/filestorage/stencils/2f3bdb9733c4a68659dc2900a7595fea.png?width=500&height=500'
                        size='tiny'
                    />
                </Menu.Item>

                <Menu.Item header
                >
                    <Header 
                        as='h2'
                        style={{
                            fontFamily: 'Arial, sans-serif',
                            color: 'white'
                        }}
                    >
                        CENTRE DE FORMATIONS
                    </Header>
                </Menu.Item>

                <Menu.Item position='right'>
                    <Menu>

                        <Menu.Item
                            name='home'
                            active={activeItem === 'home'}
                            onClick={this.handleItemClick}
                        >
                            Home
                        </Menu.Item>

                        <Menu.Item
                            name='a-propos'
                            active={activeItem === 'a-propos'}
                            onClick={this.handleItemClick}
                        >
                            A propos
                        </Menu.Item>

                        <Menu.Item
                            name='formations'
                            active={activeItem === 'formations'}
                            onClick={this.handleItemClick}
                        >
                            Formations
                        </Menu.Item>
                    </Menu>
                </Menu.Item>
            </Menu>
        )
    }
}
